import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Persona } from 'src/app/model/persona.model';
import { PersonaService } from 'src/app/services/persona.service';
import { TokenService } from 'src/app/services/token.service';

@Component({
  selector: 'app-about',
  templateUrl: './about.component.html',
  styleUrls: ['./about.component.css']
})
export class AboutComponent {

p : Persona = new Persona("","","","","");
listaPersona:Persona[]=[];
isLogged=false;

constructor(public personaService: PersonaService, private tokenService : TokenService, private router:Router){}

  ngOnInit(): void{
    this.CargarPersonas(); 
    if(this.tokenService.getToken()){
      this.isLogged=true;
    }
    else{
      this.isLogged=false;
    }
  }

  public CargarPersonas(): void{
    this.personaService.getAllPersona().subscribe(data =>{
      this.listaPersona = data;
      this.p = this.listaPersona[0];
      if(this.listaPersona[0].img=='' ||
         this.listaPersona[0].img==null ||
         this.listaPersona[0].img =="null" ||
         this.listaPersona[0].img =="")
         this.p.img="../../../assets/Img/perfil.jpg"
    })
  }

  public onEdit(i:number):void {
    this.personaService.setId(this.listaPersona[i].id);
    this.router.navigate(['/edit-per']);
  }

}
<div class="container-fluid">
    <div class="row" id="main-row">
        <div class="col-4" >
            <div class="row" id="sub-row">
                <div class="row-6">                      
                        <img src="../../../assets/Img/perfil.jpg" class="img-fluid">      
                </div>
                <div class="row-6">
                    <h1 style="margin-top: 20px;">{{p.nombre}} {{p.apellido}}</h1>
                <p>Full Stack Developer Jr</p>
                </div>                 
            </div>    
        </div>

        <div class="col-8" id="desc">
            <div class="col-11">
                <h1 style="margin-top: 30px; margin-bottom: 10px;">Acerca de mi</h1>
                <p>{{p.descripcion}}</p>
            </div>      
            <div class="col-1">  
                <div class="col-1" id=icon-edit>
                    <button type="button" class="btn btn-outline-secondary" style="width: 100px; height: 70; margin-bottom: 20px;" 
                    *ngIf="isLogged" routerLink="/new-exp" >
                        <i class="bi bi-file-earmark-plus"></i>
                        
                    </button>
                    <button type="button" class="btn btn-outline-secondary" (click)="onEdit(0)" *ngIf="isLogged">
                        <i class="bi bi-pencil-square" ></i>
                    </button>           
                </div>
            </div>      

        </div>
       
    </div>
</div>
